CREATE OR REPLACE PROCEDURE philips_patrimonio_pck.gerar_movimento_bem ( ie_commit_p text, nm_usuario_p text, pat_bem_movimento_p INOUT pat_bem_movimento) AS $body$
BEGIN
/*
Inserir um unico registro de movimento do bem
*/

select	nextval('pat_bem_movimento_seq')
into STRICT	pat_bem_movimento_p.nr_sequencia
;

pat_bem_movimento_p.nm_usuario_nrec	:= nm_usuario_p;
pat_bem_movimento_p.nm_usuario		:= nm_usuario_p;
pat_bem_movimento_p.dt_atualizacao_nrec	:= clock_timestamp();
pat_bem_movimento_p.dt_atualizacao	:= clock_timestamp();
pat_bem_movimento_p.dt_movimento	:= coalesce(pat_bem_movimento_p.dt_movimento,clock_timestamp());
pat_bem_movimento_p.vl_movimento	:= coalesce(pat_bem_movimento_p.vl_movimento,0);

insert into pat_bem_movimento values (pat_bem_movimento_p.*);

if (ie_commit_p = 'S') then
	commit;
end if;

--pat_bem_movimento_p := null;


END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
