SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE philips_patrimonio_pck.gerar_movimentos_bens ( ie_commit_p text, nm_usuario_p text, lista_movimento_p INOUT t_pat_bem_movimento) AS $body$
DECLARE


dt_atualizacao_w	timestamp;

BEGIN
/*
Inserir varios registros de movimentacao de bens em massa
Utilizar limite de 500 a 1000 registros
*/

dt_atualizacao_w := clock_timestamp();
for idx_movto in lista_movimento_p.first..lista_movimento_p.last loop
	begin
	select	nextval('pat_bem_movimento_seq')
	into STRICT	lista_movimento_p[idx_movto].nr_sequencia
	;

	lista_movimento_p[idx_movto].nm_usuario_nrec		:= nm_usuario_p;
	lista_movimento_p[idx_movto].nm_usuario			:= nm_usuario_p;
	lista_movimento_p[idx_movto].dt_atualizacao_nrec	:= dt_atualizacao_w;
	lista_movimento_p[idx_movto].dt_atualizacao		:= dt_atualizacao_w;
	lista_movimento_p[idx_movto].dt_movimento		:= coalesce(lista_movimento_p[idx_movto].dt_movimento,clock_timestamp());

	end;
end loop;

forall idx_movto in lista_movimento_p.first .. lista_movimento_p.last
	insert into pat_bem_movimento values lista_movimento_p(idx_movto);

if (ie_commit_p = 'S') then
	commit;
end if;

lista_movimento_p.delete;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
