CREATE OR REPLACE PROCEDURE pix_cancela_caixa_pck.cancelar_receb_caixa_pix ( nr_seq_caixa_rec_p bigint, nm_usuario_p text, dt_fechamento_p timestamp) AS $body$
BEGIN

        if	pix_cancela_caixa_pck.pix_obter_pendencia_caixa_rec(nr_seq_caixa_rec_p) then
        
            CALL wheb_mensagem_pck.exibir_mensagem_abort(1205393);

        else
        
          -- Cancelar recebimento PIX sem pendencia

            update	pix_cobranca
            set	dt_cancelamento	= clock_timestamp(),
				ds_status = 'CANCELADA', 			
                nm_usuario	= nm_usuario_p, 
                dt_atualizacao	= clock_timestamp() 
            where	nr_seq_caixa_rec	= nr_seq_caixa_rec_p;
        
        end if;

    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
