CREATE OR REPLACE FUNCTION pix_pck.get_mensagem_log_webhook (nr_seq_conta_banco_p banco_estab_pix.nr_seq_conta_banco%type) RETURNS varchar AS $body$
DECLARE

		ds_retorno_w	pix_webhook_historico_log.ds_log%type;
		nr_seq_log_w	pix_webhook_historico_log.nr_sequencia%type;
	
BEGIN
		select	max(b.nr_sequencia) nr_seq_log
					into STRICT nr_seq_log_w
		from 	pix_webhook_historico a,
		     		pix_webhook_historico_log b,
		     		banco_estab_pix c
		where 	a.nr_sequencia = b.nr_seq_hist_webhook
		and 		c.nr_sequencia = a.nr_seq_banco_estab_pix
		and 		c.nr_seq_conta_banco = nr_seq_conta_banco_p;
		
		select	ds_log
		into STRICT		ds_retorno_w
		from		pix_webhook_historico_log
		where	nr_sequencia = nr_seq_log_w;
		
		return ds_retorno_w;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
