SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pix_pck.valida_chave ( ds_chave_p banco_estab_pix.ds_chave%type, ie_tipo_chave_p banco_estab_pix.ie_tipo_chave%type) AS $body$
BEGIN
        if (coalesce(ds_chave_p::text, '') = '' or ie_tipo_chave_p not in ('01','02','03','04')) then
            CALL wheb_mensagem_pck.exibir_mensagem_abort(1080026);
        end if;

        if (ie_tipo_chave_p = '01') then
            CALL pix_pck.valida_telefone(ds_chave_p);
        elsif (ie_tipo_chave_p = '02') then
            CALL pix_pck.valida_email(ds_chave_p);
        elsif (ie_tipo_chave_p = '03') then
            CALL PERFORM pix_pck.valida_cpf_cnpj(ds_chave_p);
        elsif (ie_tipo_chave_p = '04') then
            CALL pix_pck.valida_chave_aleatoria(ds_chave_p);
        end if;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
