CREATE OR REPLACE PROCEDURE pix_pck.vincular_webhook (nr_seq_conta_banco_p banco_estabelecimento.nr_sequencia%type, nm_usuario_p text, ds_chave_p banco_estab_pix.ds_chave%type) AS $body$
DECLARE

		ds_retorno_integracao_w	varchar(4000);
		nr_seq_banco_estab_pix_w	banco_estab_pix.nr_sequencia%type;
		qt_registros_w	bigint;
		nr_seq_pix_webhook_w	pix_webhook.nr_sequencia%type;
	
BEGIN
		select	max(nr_sequencia)
		into STRICT		nr_seq_banco_estab_pix_w
		from		banco_estab_pix
		where	nr_seq_conta_banco = nr_seq_conta_banco_p
		and		ds_chave = ds_chave_p;
		
		select	nr_sequencia
		into STRICT		nr_seq_pix_webhook_w
		from		pix_webhook;
		
		if (coalesce(nr_seq_banco_estab_pix_w, 0) <> 0) then
			select	count(*)
			into STRICT		qt_registros_w
			from		pix_webhook_historico
			where	nr_seq_banco_estab_pix = nr_seq_banco_estab_pix_w;
			
			if (qt_registros_w = 0) then
				insert into pix_webhook_historico(
					nr_sequencia,
					nr_seq_banco_estab_pix,
					nr_seq_pix_webhook,
					dt_vinculado,
					ie_status_vinculo,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec
				)
				values (
					nextval('pix_webhook_historico_seq'),
					nr_seq_banco_estab_pix_w,				
					nr_seq_pix_webhook_w,
					clock_timestamp(),
					'I',					
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p);
			end if;
	
			commit;
			
			select BIFROST.SEND_INTEGRATION( 'pix.webhook.registration',
				'com.philips.tasy.integration.pix.api.webhook.inbound.PixWebhookRegistrationCallback',
				'{ "bankEstablishmentId": ' || nr_seq_conta_banco_p ||', "userName" : "'|| nm_usuario_p ||'", "pixKey" : "'|| ds_chave_p ||'" }',
				'integration')
			into STRICT     ds_retorno_integracao_w
			;
		end if;
		
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
