CREATE OR REPLACE FUNCTION pkg_atend_pac_unid.get_dt_dignosis_admit (P_NR_ATEND bigint, P_DT_ENTRY timestamp) RETURNS timestamp AS $body$
DECLARE

    DT timestamp;

  
BEGIN
    
    DT := NULL;

    IF coalesce(P_NR_ATEND, 0) <> 0 AND (P_DT_ENTRY IS NOT NULL AND P_DT_ENTRY::text <> '') THEN
      
      BEGIN
        SELECT MAX(D.DT_DIAGNOSTICO)
          INTO STRICT DT
          FROM DIAGNOSTICO_DOENCA D
         WHERE D.NR_ATENDIMENTO = P_NR_ATEND
           AND D.DT_DIAGNOSTICO <= P_DT_ENTRY
           AND D.IE_SITUACAO = 'A'
           AND D.IE_TIPO_DIAGNOSTICO IN (2, 3);
      EXCEPTION WHEN OTHERS THEN
        NULL;
      END;

    END IF;
    
    RETURN DT;

  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
