CREATE OR REPLACE FUNCTION pkg_atend_pac_unid.get_estab_tz (P_ESTAB bigint, P_DT timestamp DEFAULT clock_timestamp()) RETURNS varchar AS $body$
DECLARE


    IE_UTC_V varchar(15);

  
BEGIN
  
    BEGIN
      SELECT IE_UTC
        INTO STRICT IE_UTC_V
        FROM ESTAB_HORARIO_VERAO
       WHERE CD_ESTABELECIMENTO = P_ESTAB
         AND P_DT BETWEEN DT_INICIAL AND DT_FINAL;
    EXCEPTION
      WHEN OTHERS THEN
        IE_UTC_V := NULL;
    END;

    IF coalesce(IE_UTC_V::text, '') = '' THEN
    --
      BEGIN
        SELECT coalesce(IE_UTC, TO_CHAR(CURRENT_TIMESTAMP, 'TZR'))
          INTO STRICT IE_UTC_V
          FROM ESTABELECIMENTO
         WHERE CD_ESTABELECIMENTO = P_ESTAB;
      EXCEPTION
        WHEN OTHERS THEN
          IE_UTC_V := TO_CHAR(CURRENT_TIMESTAMP, 'TZR');
      END;
    --
    END IF;

    RETURN IE_UTC_V;

  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
