CREATE OR REPLACE FUNCTION pkg_atend_pac_unid.get_max_exit_unit (P_NR_ATEND bigint, P_DT_ENTRY timestamp, P_CD_SETOR bigint) RETURNS timestamp AS $body$
DECLARE

  DT timestamp;
  NR bigint;


BEGIN
      
    DT := pkg_atend_pac_unid.get_min_entry_next_unit(P_NR_ATEND, P_DT_ENTRY, P_CD_SETOR);

    IF coalesce(DT::text, '') = '' THEN
      SELECT MAX(DT_SAIDA_UNIDADE)
        INTO STRICT DT
        FROM ATEND_PACIENTE_UNIDADE U
       WHERE U.NR_ATENDIMENTO = P_NR_ATEND
         AND U.DT_SAIDA_UNIDADE >= P_DT_ENTRY
         AND U.CD_SETOR_ATENDIMENTO = P_CD_SETOR
         AND U.IE_PASSAGEM_SETOR NOT IN ('S', 'L')
         AND NOT EXISTS (SELECT 1 
                    FROM SETOR_ATENDIMENTO S
                   WHERE S.CD_SETOR_ATENDIMENTO = U.CD_SETOR_ATENDIMENTO
                     AND S.CD_CLASSIF_SETOR IN ('6', '7', '10'));
      
      IF (DT IS NOT NULL AND DT::text <> '') THEN
        SELECT COUNT(1)
          INTO STRICT NR
          FROM ATEND_PACIENTE_UNIDADE U
         WHERE U.NR_ATENDIMENTO = P_NR_ATEND
           AND U.DT_ENTRADA_UNIDADE = DT
           AND coalesce(U.DT_SAIDA_UNIDADE::text, '') = ''
           AND U.CD_SETOR_ATENDIMENTO = P_CD_SETOR
           AND U.IE_PASSAGEM_SETOR NOT IN ('S', 'L')
           AND NOT EXISTS (SELECT 1 
                      FROM SETOR_ATENDIMENTO S
                     WHERE S.CD_SETOR_ATENDIMENTO = U.CD_SETOR_ATENDIMENTO
                       AND S.CD_CLASSIF_SETOR IN ('6', '7', '10'));
        IF NR > 0 THEN
          DT := NULL;
        END IF;
      END IF;
    END IF;

    RETURN DT;

  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
