CREATE OR REPLACE FUNCTION pkg_atend_pac_unid.get_prev_unit (P_NR_ATEND bigint, P_DT_ENTRY timestamp, P_CD_SETOR bigint) RETURNS bigint AS $body$
DECLARE


  CD integer;
  DT timestamp;

  
BEGIN
  
    CD := NULL;

    IF (P_NR_ATEND IS NOT NULL AND P_NR_ATEND::text <> '') AND (P_DT_ENTRY IS NOT NULL AND P_DT_ENTRY::text <> '') AND (P_CD_SETOR IS NOT NULL AND P_CD_SETOR::text <> '') THEN
      --
      SELECT MAX(DT_ENTRADA_UNIDADE)
        INTO STRICT DT
        FROM ATEND_PACIENTE_UNIDADE U
       WHERE U.NR_ATENDIMENTO = P_NR_ATEND
         AND U.DT_ENTRADA_UNIDADE < P_DT_ENTRY
         AND U.CD_SETOR_ATENDIMENTO <> P_CD_SETOR
         AND U.IE_PASSAGEM_SETOR NOT IN ('S', 'L')
         AND NOT EXISTS (SELECT 1 
                    FROM SETOR_ATENDIMENTO S
                   WHERE S.CD_SETOR_ATENDIMENTO = U.CD_SETOR_ATENDIMENTO
                     AND S.CD_CLASSIF_SETOR IN ('6', '7', '10'));

      IF (DT IS NOT NULL AND DT::text <> '') THEN
        --
        SELECT U.CD_SETOR_ATENDIMENTO
          INTO STRICT CD
          FROM ATEND_PACIENTE_UNIDADE U
         WHERE U.NR_ATENDIMENTO = P_NR_ATEND
           AND U.DT_ENTRADA_UNIDADE = DT;
        --
      END IF;
      --
    END IF;

    RETURN CD;

  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
