CREATE OR REPLACE FUNCTION pkg_date_formaters.getuserlanguagetag (cd_estabelcimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

	ds_retorno user_locale.DS_LOCALE%TYPE;

BEGIN
  BEGIN
  
  
Select DS_LOCALE
	into STRICT   ds_retorno
	from   user_locale
	where  nm_user = nm_usuario_p;

	return ds_retorno;

  EXCEPTION
    WHEN no_data_found THEN
    begin
		Select DS_LOCALE
		into STRICT   ds_retorno
		from   ESTABLISHMENT_LOCALE
		where  CD_ESTABELECIMENTO = cd_estabelcimento_p;
    return ds_retorno;
    exception
      when no_data_found then
        return 'pt_BR';
    end;
  end;
  return 'pt_BR';
  end;


$body$
