SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pkg_date_formaters_tz.getuserlanguagetag (cd_estabelcimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

    ds_retorno user_locale.DS_LOCALE%TYPE;

BEGIN
      BEGIN
    
        Select DS_LOCALE
        into STRICT   ds_retorno
        from   user_locale
        where  nm_user = nm_usuario_p;

        return ds_retorno;

      EXCEPTION
        WHEN no_data_found THEN
        begin
            Select DS_LOCALE
            into STRICT   ds_retorno
            from   ESTABLISHMENT_LOCALE
            where  CD_ESTABELECIMENTO = cd_estabelcimento_p;
        return ds_retorno;
        exception
          when no_data_found then
		  	-- If locale data was not found and time zone support was not configured on database assumes that are Delphi or Swing customers and returns Brazil locale 'pt_BR'
            if (coalesce(TIMEZONE_UTILS.getBaseLineTimeZone()::text, '') = '') then
               return 'pt_BR';
            end if;
            RAISE EXCEPTION '%', 'Locale not defined for either the current user or his establishment (' || coalesce(cd_estabelcimento_p, -1) || ',' || coalesce(nm_usuario_p, -1) || ')' USING ERRCODE = '45011';
        end;
      end;
  end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
