CREATE OR REPLACE FUNCTION pkg_date_utils.extract_field (field text, baseDate timestamp, CALENDAR CALENDAR DEFAULT ESTABLISHMENT) RETURNS integer AS $body$
BEGIN
    CASE field
    WHEN 'YEAR' THEN
      RETURN extract(year FROM baseDate);
    WHEN 'MONTH' THEN
      RETURN extract(MONTH FROM baseDate);
    WHEN 'DAY' THEN
      RETURN extract(day FROM baseDate);
    WHEN 'HOUR' THEN
      RETURN extract(hour FROM cast(baseDate AS timestamp));
    WHEN 'MINUTE' THEN
      RETURN extract(minute FROM cast(baseDate AS timestamp));
    WHEN 'SECOND' THEN
      RETURN extract(second FROM cast(baseDate AS timestamp));
    ELSE
      RETURN NULL;
    END CASE;
  END;


