CREATE OR REPLACE FUNCTION pkg_date_utils.get_weekday (SOURCE timestamp) RETURNS bigint AS $body$
BEGIN
    CASE TO_CHAR(SOURCE, 'DY', 'NLS_DATE_LANGUAGE=ENGLISH')
    WHEN 'MON' THEN
      RETURN 2;
    WHEN 'TUE' THEN
      RETURN 3;
    WHEN 'WED' THEN
      RETURN 4;
    WHEN 'THU' THEN
      RETURN 5;
    WHEN 'FRI' THEN
      RETURN 6;
    WHEN 'SAT' THEN
      RETURN 7;
    WHEN 'SUN' THEN
      RETURN 1;
    ELSE
      RETURN 0;
    END CASE;
    RETURN 0;
  END;


