CREATE OR REPLACE FUNCTION pkg_date_utils.is_business_day ( SOURCE timestamp, LOCALE text DEFAULT null) RETURNS bigint AS $body$
BEGIN
    IF (TO_CHAR(SOURCE, 'DY', 'NLS_DATE_LANGUAGE=ENGLISH') IN ('SAT', 'SUN')) THEN
