/* Sets the minutes of the specified date, to be a multiple of the parameter 'minutes_interval', rounding down the actual minute.
    E.G.: in a 15-minutes interval, all minutes are rounded down to 0, 15, 30 or 45; in a 20-minutes interval, all minutes are rounded down to 0, 20 or 40.  */
CREATE OR REPLACE FUNCTION pkg_login_report.normalize_date (source_date timestamp, minutes_interval integer) RETURNS timestamp AS $body$
DECLARE

    minutes integer;
    start_of_date varchar(20);

BEGIN
    SELECT EXTRACT(MINUTE FROM CAST(source_date AS TIMESTAMP)) INTO STRICT minutes;
    start_of_date := TO_CHAR(source_date, 'dd/mm/yyyy hh24:');
    minutes := TRUNC(minutes / minutes_interval) * minutes_interval;
    RETURN TO_DATE(start_of_date || trim(both TO_CHAR(minutes, '00')), 'dd/mm/yyyy hh24:mi');
  END;

