CREATE OR REPLACE FUNCTION pkg_report_data.get_application_way (IE_TIPO_REL_P VIA_APLICACAO.IE_TIPO_REL%TYPE, IE_VIA_APLICACAO_P VIA_APLICACAO.IE_VIA_APLICACAO%TYPE DEFAULT NULL) RETURNS VIA_APLICACAO.IE_VIA_APLICACAO%TYPE AS $body$
DECLARE


    IE_VIA_W VIA_APLICACAO.IE_VIA_APLICACAO%TYPE;

  
BEGIN
  
    IE_VIA_W := NULL;

    IF (IE_VIA_APLICACAO_P IS NOT NULL AND IE_VIA_APLICACAO_P::text <> '') THEN
    
      IF coalesce(IE_VIA_APLICACAO_P::text, '') = '' THEN
        SELECT MAX(T.IE_VIA_APLICACAO)
          INTO STRICT IE_VIA_W
          FROM VIA_APLICACAO T
         WHERE T.IE_SITUACAO = 'A'
           AND T.IE_TIPO_REL = IE_TIPO_REL_P;
      ELSE
        SELECT MAX(T.IE_VIA_APLICACAO)
          INTO STRICT IE_VIA_W
          FROM VIA_APLICACAO T
         WHERE T.IE_SITUACAO = 'A'
           AND T.IE_TIPO_REL = IE_TIPO_REL_P
           AND T.IE_VIA_APLICACAO = IE_VIA_APLICACAO_P;
      END IF;
    END IF;

    RETURN IE_VIA_W;

  END;

$body$
