CREATE OR REPLACE FUNCTION pkg_report_data.get_procedure (IE_TIPO_REL_P PROCEDIMENTO.IE_TIPO_REL%TYPE, CD_PROCEDIMENTO_P PROCEDIMENTO.CD_PROCEDIMENTO%TYPE DEFAULT NULL, IE_ORIGEM_PROCED_P PROCEDIMENTO.IE_ORIGEM_PROCED%TYPE DEFAULT NULL) RETURNS PROCEDIMENTO.CD_PROCEDIMENTO%TYPE AS $body$
DECLARE


    CD_PROCEDIMENTO_W PROCEDIMENTO.CD_PROCEDIMENTO%TYPE;

  
BEGIN
  
    CD_PROCEDIMENTO_W := NULL;

    IF (IE_TIPO_REL_P IS NOT NULL AND IE_TIPO_REL_P::text <> '') THEN
    
      IF coalesce(CD_PROCEDIMENTO_P::text, '') = '' THEN
        SELECT MAX(CD_PROCEDIMENTO)
          INTO STRICT CD_PROCEDIMENTO_W
          FROM PROCEDIMENTO T
         WHERE T.IE_SITUACAO = 'A'
           AND T.IE_TIPO_REL = IE_TIPO_REL_P;
      ELSE
        SELECT MAX(CD_PROCEDIMENTO)
          INTO STRICT CD_PROCEDIMENTO_W
          FROM PROCEDIMENTO T
         WHERE T.IE_SITUACAO = 'A'
           AND T.IE_TIPO_REL = IE_TIPO_REL_P
           AND T.CD_PROCEDIMENTO= CD_PROCEDIMENTO_P
           AND T.IE_ORIGEM_PROCED = IE_ORIGEM_PROCED_P;
      END IF;
    END IF;

    RETURN CD_PROCEDIMENTO_W;

  END;

$body$
