DECLARE


    NR_SEQ_W GQA_PENDENCIA_REGRA.NR_SEQUENCIA%TYPE;

  
BEGIN
  
    NR_SEQ_W := NULL;

    IF (IE_TIPO_PROTOCOLO_ETAPA_REL_P IS NOT NULL AND IE_TIPO_PROTOCOLO_ETAPA_REL_P::text <> '') THEN
    
      IF coalesce(NR_SEQUENCIA_P::text, '') = '' THEN
    
        SELECT MAX(NR_SEQUENCIA)
