          FROM GQA_PENDENCIA_REGRA T
         WHERE T.IE_SITUACAO = 'A'
           AND T.IE_TIPO_PROTOCOLO_ETAPA_REL = IE_TIPO_PROTOCOLO_ETAPA_REL_P;

      ELSE
      
        SELECT MAX(NR_SEQUENCIA)
          INTO STRICT NR_SEQ_W
          FROM GQA_PENDENCIA_REGRA T
         WHERE T.IE_SITUACAO = 'A'
           AND T.IE_TIPO_PROTOCOLO_ETAPA_REL = IE_TIPO_PROTOCOLO_ETAPA_REL_P
           AND T.NR_SEQUENCIA = NR_SEQUENCIA_P;

