CREATE OR REPLACE FUNCTION pkg_statistics_report.get_first_entry (P_NR_ATEND bigint, P_CD_SETOR bigint) RETURNS timestamp AS $body$
DECLARE


  DT timestamp;

  
BEGIN
  
    IF (P_NR_ATEND IS NOT NULL AND P_NR_ATEND::text <> '') AND (P_CD_SETOR IS NOT NULL AND P_CD_SETOR::text <> '') THEN
      --
      SELECT MIN(DT_ENTRADA_UNIDADE)
        INTO STRICT DT
        FROM ATEND_PACIENTE_UNIDADE U
       WHERE U.NR_ATENDIMENTO = P_NR_ATEND
         AND CASE WHEN P_CD_SETOR=0 THEN  0  ELSE U.CD_SETOR_ATENDIMENTO END  = P_CD_SETOR
         AND U.IE_PASSAGEM_SETOR NOT IN ('S', 'L');
      --
    END IF;

    RETURN DT;

  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
