	--Contas de Emergncia 
CREATE OR REPLACE PROCEDURE planserv_desdobramento_cta_pck.processar_emergencia ( nr_interno_conta_p conta_paciente.nr_interno_conta%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
