SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_a500_pck.gerencia_analise_pend_a500 ( nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Carrega as faturas A500 que estao pendentes de gerar as analises
	
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [X] Tasy (Delphi/Java) [X] Portal [X]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


nm_id_sid_w		ptu_fatura.ds_sid_processo%type;
nm_id_serial_w		ptu_fatura.ds_serial_processo%type;

c01 CURSOR FOR
	SELECT	a.nr_seq_fatura,
		a.cd_estabelecimento,
		a.nm_usuario
	from	pls_a500_analise_pend_gerar_v	a
	order by	a.dt_vencimento_fatura asc,
			a.vl_total_fatura asc;
	
BEGIN
CALL wheb_usuario_pck.set_nm_usuario(coalesce(nm_usuario_p, 'Tasy'));

-- Verificar se tem fatura com falha de conexao na geracao da analise

CALL pls_a500_pck.verifica_falha_conexao_analise();

select	max(sid),
	max(serial#)
into STRICT	nm_id_sid_w,
	nm_id_serial_w
from	v$session
where 	audsid = userenv('SESSIONID');

for r_c01_w in c01 loop
	update	ptu_fatura
	set	ds_sid_processo		= nm_id_sid_w,
		ds_serial_processo	= nm_id_serial_w
	where	nr_sequencia		= r_c01_w.nr_seq_fatura;

	CALL pls_a500_pck.gerar_analise_pend_a500( r_c01_w.nr_seq_fatura, r_c01_w.cd_estabelecimento, r_c01_w.nm_usuario);
end loop;

-- Verificar se tem fatura com falha de conexao na geracao da analise

CALL pls_a500_pck.verifica_falha_conexao_analise();

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
