CREATE OR REPLACE FUNCTION pls_a500_pck.qt_inconsistencia_fat ( nr_seq_fatura_p ptu_fatura.nr_sequencia%type) RETURNS integer AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Retorna a quantidade de inconsistencias controladas via regra encontradas na fatura
	
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [X] Tasy (Delphi/Java) [X] Portal [X]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */

qt_inconsistencia_w	integer;

BEGIN

select	count(1)
into STRICT	qt_inconsistencia_w
from	ptu_fatura_consistencia	a
where	a.nr_seq_fatura		= nr_seq_fatura_p;

return qt_inconsistencia_w;

END;

$body$
