SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_agrupamento_analise_pck.glosar_atendimento_analise ( nr_seq_analise_p pls_analise_conta.nr_sequencia%type, nm_usuario_p text, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


nr_seq_regra_an_agrup_w		pls_conta_v.nr_seq_regra_an_agrup%type;
nr_seq_grupo_atual_w		pls_auditoria_conta_grupo.nr_seq_grupo%type;
nr_seq_motivo_glosa_w		pls_regra_an_agrup.nr_seq_motivo_glosa%type;
nr_seq_protocolo_w			pls_protocolo_conta.nr_sequencia%type;
ds_retorno_w				varchar(400);
C01 CURSOR(nr_seq_analise_pc	pls_analise_conta.nr_sequencia%type)FOR
	SELECT	conta.nr_sequencia,
		coalesce(conta.nr_seq_lote_conta_orig, prot.nr_seq_lote_conta) nr_seq_lote_conta_orig,
		prot.nr_seq_prestador,
		prot.ie_tipo_guia,
		prot.nr_sequencia nr_seq_protocolo
	from	pls_conta		conta,
		pls_protocolo_conta	prot
	where	conta.nr_seq_analise	= nr_seq_analise_pc
	and	conta.nr_seq_protocolo	= prot.nr_sequencia;
BEGIN

select 	max(nr_seq_regra_an_agrup)
into STRICT	nr_seq_regra_an_agrup_w
from	pls_conta_v
where	nr_seq_analise	= nr_seq_analise_p;

select	max(nr_seq_motivo_glosa)
into STRICT	nr_seq_motivo_glosa_w
from	pls_regra_an_agrup
where	nr_sequencia = nr_seq_regra_an_agrup_w;


if (nr_seq_motivo_glosa_w IS NOT NULL AND nr_seq_motivo_glosa_w::text <> '') then
	CALL pls_inserir_w_glosa_ocor(nr_seq_analise_p,null,nr_seq_motivo_glosa_w,
				null,nm_usuario_p,'S');

	select  min(a.nr_seq_grupo)
	into STRICT	nr_seq_grupo_atual_w
	from    pls_auditoria_conta_grupo a
	where   a.nr_seq_analise = nr_seq_analise_p;
	
	if (coalesce(nr_seq_grupo_atual_w::text, '') = '') then
		begin
		select 	max(og.nr_seq_grupo)
		into STRICT	nr_seq_grupo_atual_w
		from	pls_ocorrencia		oc,
			pls_ocorrencia_grupo	og
		where 	oc.nr_seq_motivo_glosa 	= nr_seq_motivo_glosa_w
		and	oc.nr_sequencia		= og.nr_seq_ocorrencia
		and	og.ie_conta_medica	= 'S'
		and	og.ie_situacao		= 'A';
		end;
	end if;

	CALL pls_analise_glosar_atend(	nr_seq_analise_p,null, null,
					nr_seq_grupo_atual_w,null, null,
					nm_usuario_p, cd_estabelecimento_p);
					
	for r_c01_w in C01(nr_seq_analise_p) loop
	begin
		select	max(nr_sequencia)
		into STRICT	nr_seq_protocolo_w
		from	pls_protocolo_conta
		where	nr_seq_lote_conta	= r_c01_w.nr_seq_lote_conta_orig
		and	nr_seq_prestador	= r_c01_w.nr_seq_prestador
		and	ie_tipo_guia		= r_c01_w.ie_tipo_guia;
	
		if (coalesce(nr_seq_protocolo_w::text, '') = '') then
			begin
			SELECT * FROM pls_agrupamento_analise_pck.gera_protocolo_conta(	r_c01_w.nr_seq_protocolo, r_c01_w.ie_tipo_guia, r_c01_w.nr_seq_lote_conta_orig, nm_usuario_p, cd_estabelecimento_p, nr_seq_protocolo_w, ds_retorno_w) INTO STRICT nr_seq_protocolo_w, ds_retorno_w;
	        end;
        end if;
	
		update	pls_conta
		set	nr_seq_protocolo	= nr_seq_protocolo_w
		where	nr_sequencia		= r_c01_w.nr_sequencia;

		update	pls_analise_conta
		set	nr_seq_lote_protocolo	= r_c01_w.nr_seq_lote_conta_orig
		where	nr_sequencia		= nr_seq_analise_p;
	
		/* Gerar valores */


		CALL pls_gerar_valores_protocolo(nr_seq_protocolo_w, nm_usuario_p);
		/* Alterar status */


		CALL pls_altera_status_protocolo(nr_seq_protocolo_w, 'L', 'N', cd_estabelecimento_p, nm_usuario_p);
	
		end;
	end loop;
end if;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_agrupamento_analise_pck.glosar_atendimento_analise ( nr_seq_analise_p pls_analise_conta.nr_sequencia%type, nm_usuario_p text, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) FROM PUBLIC;
