CREATE OR REPLACE PROCEDURE pls_agrupamento_analise_pck.glosar_atendimento_lote ( nr_seq_lote_p pls_lote_protocolo_conta.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Buscar as analises geradas e chamar a glosar_atendimento_analise, que vai identificar qual e
	o agrupamento e gerar as glosas
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */

-- carrega as analises do lote de atendimento

c01 CURSOR(	nr_seq_lote_pc	pls_lote_protocolo_conta.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia
	from	pls_analise_conta a
	where	nr_seq_lote_protocolo = nr_seq_lote_pc;
BEGIN

if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then

	-- abre as analises pertinentes ao atendimento (lote)

	for r_c01_w in c01(nr_seq_lote_p) loop
		
		CALL pls_agrupamento_analise_pck.glosar_atendimento_analise(r_c01_w.nr_sequencia, nm_usuario_p, cd_estabelecimento_p);
		
		
	end loop; -- fim percorrer as analises
end if;

END;

$body$
