SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_agrupamento_analise_pck.verifica_regra_agrup_analise ( nr_seq_lote_p pls_lote_protocolo_conta.nr_sequencia%type, nm_usuario_p text, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


nr_seq_analise_n_w	pls_analise_conta.nr_sequencia%type;

C_conta CURSOR(nr_seq_lote_pc		pls_protocolo_conta.nr_seq_lote_conta%type) FOR
	SELECT	nr_sequencia,
		cd_guia_ref cd_guia_referencia,
		nr_seq_segurado,
		nr_seq_lote_conta,
		nr_seq_analise,
		nr_seq_regra_an_agrup
	from	pls_conta_v
	where	nr_seq_lote_conta 	= nr_seq_lote_pc
	and	(nr_seq_regra_an_agrup IS NOT NULL AND nr_seq_regra_an_agrup::text <> '')
	and	ie_status		!= 'C'
	order by nr_sequencia;
BEGIN

for r_c_conta_w in C_conta(nr_seq_lote_p) loop
	begin
	select 	max(nr_sequencia)
	into STRICT	nr_seq_analise_n_w
	from	pls_analise_conta
	where	nr_seq_segurado	= r_c_conta_w.nr_seq_segurado
	and	cd_guia		= r_c_conta_w.cd_guia_referencia
	and	ie_origem_analise = 1
	and	ie_tipo_guia	= '5'
	and	ie_status	= 'G';
	
	if (nr_seq_analise_n_w IS NOT NULL AND nr_seq_analise_n_w::text <> '') then
		begin
		CALL pls_agrupamento_analise_pck.vincula_conta_analise_exist( 	r_c_conta_w.nr_sequencia, nr_seq_analise_n_w, nm_usuario_p,
						cd_estabelecimento_p);
		--#diogoAqui tratar para excluir a analise original

		end;
	else
		begin
		
		select 	max(a.nr_sequencia)
		into STRICT	nr_seq_analise_n_w
		from	pls_analise_conta a
		where	a.nr_seq_segurado 	= r_c_conta_w.nr_seq_segurado
		and	a.cd_guia 		= r_c_conta_w.cd_guia_referencia
		and	a.ie_origem_analise 	= 1
		and	a.ie_tipo_guia		= '5'
		and	a.ie_status		<> 'C' --Se tiver uma analise de guia de resumo que estiver cancelada, entao nao considera e nao glosa o atendimento indevidamente

		and	exists (	SELECT	1
				from	pls_conta x
				where	x.nr_seq_analise = a.nr_sequencia
				and	x.ie_tipo_faturamento in ('T', 'F'));
		--verifica se existe analise caso existir glosa o atendimento com a glosa informada na regra

		if (nr_seq_analise_n_w IS NOT NULL AND nr_seq_analise_n_w::text <> '') then
			begin
			CALL pls_agrupamento_analise_pck.glosar_atendimento_analise(	r_c_conta_w.nr_seq_analise,nm_usuario_p,cd_estabelecimento_p);
			end;
		else
			begin
			--Nao existindo  criar um novo lote e um novo protocolo e vinculo e setar a analise como "aguardando guia"

			CALL pls_agrupamento_analise_pck.vincula_conta_aguardando_guia( 	r_c_conta_w.nr_sequencia,r_c_conta_w.nr_seq_analise,nr_seq_lote_p,
							nm_usuario_p, cd_estabelecimento_p);
				
			end;
		end if;
		end;
	end if;
	
	end;
end loop;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_agrupamento_analise_pck.verifica_regra_agrup_analise ( nr_seq_lote_p pls_lote_protocolo_conta.nr_sequencia%type, nm_usuario_p text, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) FROM PUBLIC;
