CREATE OR REPLACE FUNCTION pls_agrupamento_analise_pck.verificar_existencia_regra (cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE


qt_registro_w	integer;
ds_retorno_w	varchar(1)	:= 'N';

BEGIN

select 	count(1)
into STRICT	qt_registro_w
from	pls_regra_an_agrup
where 	cd_estabelecimento	= cd_estabelecimento_p;

if (qt_registro_w	> 0) then
	begin
	ds_retorno_w	:= 'S';	
	end;
end if;
return	ds_retorno_w;

END;

$body$
