--Atualiza tipo guia em todas as an?lises e atualza refer?ncia na tabela pls_conta.



CREATE OR REPLACE PROCEDURE pls_analise_cta_pck.atualiza_analise_conta ( tb_nr_seq_analise_update_p dbms_sql.number_table, tb_seq_conta_p dbms_sql.number_table, tb_tipo_guia_update_p dbms_sql.varchar2_table) AS $body$
BEGIN

if (tb_nr_seq_analise_update_p.count > 0) then

  forall i in tb_nr_seq_analise_update_p.first..tb_nr_seq_analise_update_p.last
    update  pls_analise_conta
    set ie_tipo_guia  = tb_tipo_guia_update_p(i)
    where nr_sequencia  = tb_nr_seq_analise_update_p(i)
    and (ie_tipo_guia <> '5'
    or  coalesce(ie_tipo_guia::text, '') = '');

  commit;

  forall i in tb_nr_seq_analise_update_p.first..tb_nr_seq_analise_update_p.last   
    update  pls_conta
    set nr_seq_analise  = tb_nr_seq_analise_update_p(i)
    where nr_sequencia  = tb_seq_conta_p(i);
  commit;
end if;

END;

$body$
