SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_apropriacao_pck.get_ie_status_mensalidade (nr_seq_guia_p pls_guia_plano.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE

	ie_cobrar_copartic_w	pls_requisicao.ie_cobrar_copartic%type;
	ie_status_mensalidade_w	varchar(1);

BEGIN
	select	max(b.ie_cobrar_copartic)
	into STRICT	ie_cobrar_copartic_w
	from	pls_execucao_req_item	a,
		pls_requisicao		b
	where	a.nr_seq_requisicao	= b.nr_sequencia
	and	a.nr_seq_guia		= nr_seq_guia_p;
	
	if (coalesce(ie_cobrar_copartic_w,'S') = 'N') then
		ie_status_mensalidade_w	:= 'C';
	else
		if (coalesce(current_setting('pls_apropriacao_pck.pls_parametros_w')::pls_parametros%rowtype.ie_controle_coparticipacao,'N') = 'S') then
			ie_status_mensalidade_w := 'P';
		else
			if (get_ie_status_item in ('L','S')) then
				ie_status_mensalidade_w := 'L';
			else
				ie_status_mensalidade_w := 'P';
			end if;
		end if;
	end if;
	
	return ie_status_mensalidade_w;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
