SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_apropriacao_pck.pls_gerar_apropriacao_conta ( nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

	
	procedimentos_conta CURSOR FOR
		SELECT	nr_sequencia
		from	pls_conta_proc
		where	nr_seq_conta	= nr_seq_conta_p
		and	((nr_sequencia	= nr_seq_conta_proc_p) or (coalesce(nr_seq_conta_proc_p::text, '') = ''))
		order by vl_liberado desc;
	
	materiais_conta CURSOR FOR
		SELECT	nr_sequencia
		from	pls_conta_mat
		where	nr_seq_conta	= nr_seq_conta_p
		and	((nr_sequencia	= nr_seq_conta_mat_p) or (coalesce(nr_seq_conta_mat_p::text, '') = ''))
		order by vl_liberado desc;

BEGIN

PERFORM set_config('pls_apropriacao_pck.vl_fixo_aprop_conta_w', 0, false);
PERFORM set_config('pls_apropriacao_pck.vl_pendente_aprop_w', 0, false);
PERFORM set_config('pls_apropriacao_pck.nr_centro_nao_aprop_w', 0, false);
PERFORM set_config('pls_apropriacao_pck.ie_primeira_vez_w', 'S', false);
PERFORM set_config('pls_apropriacao_pck.nr_seq_regra_aprop_ant_w', 0, false);
PERFORM set_config('pls_apropriacao_pck.nr_seq_proc_rec_w', null, false);
PERFORM set_config('pls_apropriacao_pck.nr_seq_mat_rec_w', null, false);
PERFORM set_config('pls_apropriacao_pck.ie_recurso_glosa_w', 'N', false);
current_setting('pls_apropriacao_pck.vl_pendente_guia_table_w')::pls_util_cta_pck.t_number_table.delete;



delete	FROM pls_conta_pos_estab_aprop
where	nr_seq_conta_pos_estab in (	SELECT	nr_sequencia
					from	pls_conta_pos_estabelecido
					where	nr_seq_conta	= nr_seq_conta_p);
			
for procedimentos_conta_w in procedimentos_conta loop
	CALL CALL pls_apropriacao_pck.pls_gerar_apropriacao(procedimentos_conta_w.nr_sequencia, null, null, null, 'N', nm_usuario_p);
end loop;

for materiais_conta_w in materiais_conta loop
	CALL CALL pls_apropriacao_pck.pls_gerar_apropriacao(null, materiais_conta_w.nr_sequencia, null, null, 'N', nm_usuario_p);
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_apropriacao_pck.pls_gerar_apropriacao_conta ( nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;
