CREATE OR REPLACE PROCEDURE pls_ar_gerar_resultado_pck.finalizar_geracao ( nr_seq_lote_p pls_ar_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_situacao_p pls_ar_lote.ie_situacao%type) AS $body$
DECLARE

/*	ie_situacao
	A - Aguardando geracao
	G - Gerado
	C - Concluido
*/


BEGIN

-- Concluido

if (ie_situacao_p	= 'C') then
	update	pls_ar_lote
	set	dt_geracao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp(),
		ie_situacao		= 'C'
	where	nr_sequencia 		= nr_seq_lote_p;
end if; -- fim concluido

-- Aguardando

if (ie_situacao_p	= 'A') then
	update	pls_ar_lote
	set	dt_geracao		 = NULL,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp(),
		ie_situacao		= 'A'
	where	nr_sequencia 		= nr_seq_lote_p;
end if; -- fim Aguardando


END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
