CREATE OR REPLACE PROCEDURE pls_ar_gerar_resultado_pck.grava_log_geral ( nr_seq_lote_p pls_ar_lote.nr_sequencia%type, ds_log_p pls_ar_lote.ds_log%type, ie_opcao text) AS $body$
DECLARE

/*	ie_opcao
	S = Sobreescrever
	A = Adicionar
*/


BEGIN

update	pls_ar_lote
set	ds_log		= CASE WHEN ie_opcao='S' THEN  ds_log_p WHEN ie_opcao='A' THEN  ds_log||' '||ds_log_p END
where	nr_sequencia	= nr_seq_lote_p;

commit;

END;

