CREATE OR REPLACE PROCEDURE pls_ar_gerar_resultado_pck.rateio_despesas_contabeis ( nr_seq_lote_p pls_ar_lote.nr_sequencia%type, dt_inicio_p pls_ar_lote.dt_inicio%type, dt_fim_p pls_ar_lote.dt_fim%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.ie_tipo_despesa,
		a.ie_tipo_rateio,
		a.qt_beneficiarios,
		a.vl_despesa,
		a.vl_base_mensalidade,
		(	SELECT	count(1)
			from	pls_ar_desp_contab_regra x
			where	x.nr_seq_ar_desp_contab	= a.nr_sequencia) qt_regra
	from	pls_ar_despesa_contab_lote a
	where	a.nr_seq_lote	= nr_seq_lote_p
	and	a.vl_despesa <> 0;

BEGIN

for r_c01_w in C01 loop
	begin
	if (r_c01_w.ie_tipo_rateio = 'P') then --Rateio proporcional conforme valor de mensalidade
		CALL pls_ar_gerar_resultado_pck.rateio_desp_contab_prop(nr_seq_lote_p, r_c01_w.nr_sequencia, r_c01_w.ie_tipo_despesa, r_c01_w.vl_despesa, r_c01_w.vl_base_mensalidade, r_c01_w.qt_regra, dt_fim_p, nm_usuario_p, cd_estabelecimento_p);
	elsif (r_c01_w.ie_tipo_rateio = 'A') then --Rateio entre beneficiarios ativos
		CALL pls_ar_gerar_resultado_pck.rateio_desp_contab_ativos(nr_seq_lote_p, r_c01_w.nr_sequencia, r_c01_w.ie_tipo_despesa, r_c01_w.vl_despesa, r_c01_w.qt_beneficiarios, r_c01_w.qt_regra, dt_fim_p, nm_usuario_p, cd_estabelecimento_p);
	end if;
	
	--Arredondamento

	CALL pls_ar_gerar_resultado_pck.arredondamento_despesas_contab(r_c01_w.nr_sequencia, r_c01_w.vl_despesa);
	end;
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
