SET client_encoding TO 'UTF8';


-- SELECIONAR_COMISSAO_REPASSE



CREATE OR REPLACE PROCEDURE pls_ar_gerar_resultado_pck.selecionar_comissao_repasse ( nr_seq_lote_p pls_ar_lote.nr_sequencia%type, dt_inicio_p pls_ar_lote.dt_inicio%type, dt_fim_p pls_ar_lote.dt_fim%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
BEGIN

	begin
		insert into pls_ar_comissao(nr_sequencia,
			nm_usuario,
			nm_usuario_nrec,
			dt_atualizacao,
			dt_atualizacao_nrec,
			cd_estabelecimento,
			nr_seq_lote,
			nr_seq_segurado,
			nr_seq_repasse_mens,
			vl_comissao,
			nr_seq_plano,
			ie_tipo_segurado,
			ie_tipo_vinculo_operadora,
			nr_seq_contrato,
			nr_seq_pagador,
			nr_seq_intercambio,
			ie_preco,
			ie_regulamentacao,
			ie_tipo_contratacao,
			nr_seq_faixa_etaria,
			ie_situacao_trabalhista,
			nr_seq_congenere_repasse,
			ie_tipo_contrato,
			nr_seq_grupo_intercambio)
		SELECT	nextval('pls_ar_comissao_seq'),
			nm_usuario_p,
			nm_usuario_p,
			clock_timestamp(),
			clock_timestamp(),
			cd_estabelecimento_p,
			nr_seq_lote_p,
			t.nr_seq_segurado,
			t.nr_seq_repasse_mens,
			coalesce(t.vl_repasse,0) vl_repasse,
			coalesce(t.nr_seq_plano, a.nr_seq_plano) nr_seq_plano,
			coalesce(a.ie_tipo_segurado,'B') ie_tipo_segurado,
			a.ie_tipo_vinculo_operadora ie_tipo_vinculo_operadora,
			a.nr_seq_contrato nr_seq_contrato,
			a.nr_seq_pagador nr_seq_pagador,
			a.nr_seq_intercambio nr_seq_intercambio,
			b.ie_preco ie_preco,
			b.ie_regulamentacao ie_regulamentacao,
			b.ie_tipo_contratacao ie_tipo_contratacao,
			c.nr_seq_faixa_etaria nr_seq_faixa_etaria,
			a.ie_situacao_trabalhista ie_situacao_trabalhista,
			(	SELECT	x.nr_seq_congenere
				from	pls_segurado_repasse	x
				where	x.nr_seq_segurado	= t.nr_seq_segurado
				and	t.dt_referencia between x.dt_repasse and coalesce(x.dt_fim_repasse, clock_timestamp())
			 LIMIT 1) nr_seq_congenere_repasse,
			case when (a.nr_seq_contrato IS NOT NULL AND a.nr_seq_contrato::text <> '')
					then CASE WHEN coalesce(e.cd_cgc_estipulante::text, '') = '' THEN  'PF'  ELSE 'PJ' END 
				else (case when (a.nr_seq_intercambio IS NOT NULL AND a.nr_seq_intercambio::text <> '')
						then CASE WHEN coalesce(f.cd_cgc::text, '') = '' THEN  'PF'  ELSE 'PJ' END 
					else null
				end)
			end ie_tipo_contrato,
			f.nr_seq_grupo_intercambio nr_seq_grupo_intercambio
		FROM (	select	coalesce(sum(vl_repasse), 0) vl_repasse,
				c.nr_seq_segurado,
				b.nr_sequencia nr_seq_repasse_mens,
				c.nr_seq_plano,
				a.dt_referencia
			from	pls_mensalidade_segurado	c,
				pls_repasse_mens		b,
				pls_repasse_vend		a
			where  	a.nr_sequencia			= b.nr_seq_repasse
			and	c.nr_sequencia			= b.nr_seq_mens_seg
			and	a.dt_referencia between dt_inicio_p and dt_fim_p
			and	a.ie_status = 'F'
			group by
				c.nr_seq_segurado,
				b.nr_sequencia,
				c.nr_seq_plano,
				a.dt_referencia) t, pls_plano b, pls_segurado a
LEFT OUTER JOIN pls_contrato e ON (a.nr_seq_contrato = e.nr_sequencia)
LEFT OUTER JOIN pls_intercambio f ON (a.nr_seq_intercambio = f.nr_sequencia)
LEFT OUTER JOIN pls_tabela_preco c ON (a.nr_seq_tabela = c.nr_sequencia)
WHERE a.nr_sequencia		= t.nr_seq_segurado and b.nr_sequencia		= t.nr_seq_plano;
	
	commit;
	exception
		when no_data_found then null;
	end;
	
commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_ar_gerar_resultado_pck.selecionar_comissao_repasse ( nr_seq_lote_p pls_ar_lote.nr_sequencia%type, dt_inicio_p pls_ar_lote.dt_inicio%type, dt_fim_p pls_ar_lote.dt_fim%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) FROM PUBLIC;
