SET client_encoding TO 'UTF8';



-- SELECIONAR_CONTESTACAO



CREATE OR REPLACE PROCEDURE pls_ar_gerar_resultado_pck.selecionar_contestacao ( nr_seq_lote_p pls_ar_lote.nr_sequencia%type, dt_inicio_p pls_ar_lote.dt_inicio%type, dt_fim_p pls_ar_lote.dt_fim%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
BEGIN

	begin
		insert into pls_ar_contestacao(nr_sequencia,
			nm_usuario,
			nm_usuario_nrec,
			dt_atualizacao,
			dt_atualizacao_nrec,
			cd_estabelecimento,
			nr_seq_lote,
			vl_contestacao,
			nr_seq_segurado,
			nr_seq_discussao,
			nr_seq_plano,
			ie_tipo_segurado,
			ie_tipo_vinculo_operadora,
			nr_seq_contrato,
			nr_seq_pagador,
			nr_seq_intercambio,
			ie_preco,
			ie_regulamentacao,
			ie_tipo_contratacao,
			nr_seq_faixa_etaria,
			ie_situacao_trabalhista,
			nr_seq_congenere_repasse,
			ie_tipo_contrato,
			nr_seq_grupo_intercambio,
			nr_seq_conta)
		SELECT	nextval('pls_ar_contestacao_seq'),
			nm_usuario_p,
			nm_usuario_p,
			clock_timestamp(),
			clock_timestamp(),
			cd_estabelecimento_p,
			nr_seq_lote_p,
			coalesce(t.vl_total_recursado,0),
			t.nr_seq_segurado,
			t.nr_seq_discussao,
			coalesce(t.nr_seq_plano, a.nr_seq_plano) nr_seq_plano,
			coalesce(t.ie_tipo_segurado, coalesce(a.ie_tipo_segurado,'B')) ie_tipo_segurado,
			a.ie_tipo_vinculo_operadora ie_tipo_vinculo_operadora,
			a.nr_seq_contrato nr_seq_contrato,
			a.nr_seq_pagador nr_seq_pagador,
			a.nr_seq_intercambio nr_seq_intercambio,
			b.ie_preco ie_preco,
			b.ie_regulamentacao ie_regulamentacao,
			b.ie_tipo_contratacao ie_tipo_contratacao,
			c.nr_seq_faixa_etaria nr_seq_faixa_etaria,
			a.ie_situacao_trabalhista ie_situacao_trabalhista,
			(	SELECT	x.nr_seq_congenere
				from	pls_segurado_repasse	x
				where	x.nr_seq_segurado	= t.nr_seq_segurado
				and	t.dt_competencia_lote between x.dt_repasse and coalesce(x.dt_fim_repasse, clock_timestamp())
			 LIMIT 1) nr_seq_congenere_repasse,
			case when (a.nr_seq_contrato IS NOT NULL AND a.nr_seq_contrato::text <> '')
					then CASE WHEN coalesce(e.cd_cgc_estipulante::text, '') = '' THEN  'PF'  ELSE 'PJ' END 
				else (case when (a.nr_seq_intercambio IS NOT NULL AND a.nr_seq_intercambio::text <> '')
						then CASE WHEN coalesce(f.cd_cgc::text, '') = '' THEN  'PF'  ELSE 'PJ' END 
					else null
				end)
			end ie_tipo_contrato,
			f.nr_seq_grupo_intercambio nr_seq_grupo_intercambio,
			t.nr_seq_conta
		FROM (	select	sum(coalesce(dp.vl_contestado,0)) - sum(coalesce(vl_ndc,0)) vl_total_recursado,
				f.nr_seq_segurado,
				cd.nr_sequencia nr_seq_discussao,
				f.nr_seq_plano,
				ld.dt_referencia dt_competencia_lote,
				cc.nr_seq_conta,
				f.ie_tipo_segurado
			from	pls_contestacao			cc,
				pls_lote_contestacao		lc,
				pls_lote_discussao		ld,
				pls_contestacao_discussao	cd,
				pls_discussao_proc		dp,
				pls_conta			f
			where	ld.dt_fechamento between dt_inicio_p and dt_fim_p
			and	lc.nr_sequencia		= cc.nr_seq_lote
			and	lc.nr_sequencia		= ld.nr_seq_lote_contest
			and	ld.nr_sequencia		= cd.nr_seq_lote
			and	cd.nr_sequencia		= dp.nr_seq_discussao
			and	f.nr_sequencia		= cc.nr_seq_conta
			and 	cc.nr_sequencia		= cd.nr_seq_contestacao
			and	(lc.nr_seq_ptu_fatura IS NOT NULL AND lc.nr_seq_ptu_fatura::text <> '')-- Fatura importada
			and	lc.ie_status   		= 'C'	-- Lote concluido

			and	cc.vl_atual		> 0	-- Tem saldo
			and	lc.ie_processo_pgto	= 'I'	-- Pagamento Integral

			group by f.nr_seq_segurado,
				cd.nr_sequencia,
				f.nr_seq_plano,
				ld.dt_referencia,
				cc.nr_seq_conta,
				f.ie_tipo_segurado) t, pls_plano b, pls_segurado a
LEFT OUTER JOIN pls_contrato e ON (a.nr_seq_contrato = e.nr_sequencia)
LEFT OUTER JOIN pls_intercambio f ON (a.nr_seq_intercambio = f.nr_sequencia)
LEFT OUTER JOIN pls_tabela_preco c ON (a.nr_seq_tabela = c.nr_sequencia)
WHERE a.nr_sequencia		= t.nr_seq_segurado and b.nr_sequencia		= t.nr_seq_plano;
		
		commit;
	exception
		when no_data_found then null;
	end;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_ar_gerar_resultado_pck.selecionar_contestacao ( nr_seq_lote_p pls_ar_lote.nr_sequencia%type, dt_inicio_p pls_ar_lote.dt_inicio%type, dt_fim_p pls_ar_lote.dt_fim%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) FROM PUBLIC;
