SET client_encoding TO 'UTF8';


-- SELECIONAR_COPARTICIPACOES



CREATE OR REPLACE PROCEDURE pls_ar_gerar_resultado_pck.selecionar_coparticipacoes ( nr_seq_lote_p pls_ar_lote.nr_sequencia%type, dt_inicio_p pls_ar_lote.dt_inicio%type, dt_fim_p pls_ar_lote.dt_fim%type, ie_data_coparticipacao_p pls_parametros_ar.ie_data_coparticipacao%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
BEGIN

	begin
		insert into pls_ar_coparticipacao(nr_sequencia,
			nm_usuario,
			nm_usuario_nrec,
			dt_atualizacao,
			dt_atualizacao_nrec,
			cd_estabelecimento,
			nr_seq_lote,
			vl_coparticipacao,
			nr_seq_segurado,
			nr_seq_copartic,
			nr_seq_conta,
			nr_seq_plano,
			ie_tipo_segurado,
			ie_tipo_vinculo_operadora,
			nr_seq_contrato,
			nr_seq_pagador,
			nr_seq_intercambio,
			ie_preco,
			ie_regulamentacao,
			ie_tipo_contratacao,
			nr_seq_faixa_etaria,
			ie_situacao_trabalhista,
			nr_seq_congenere_repasse,
			ie_tipo_contrato,
			nr_seq_grupo_intercambio)
		SELECT	nextval('pls_ar_coparticipacao_seq'),
			nm_usuario_p,
			nm_usuario_p,
			clock_timestamp(),
			clock_timestamp(),
			cd_estabelecimento_p,
			nr_seq_lote_p,
			coalesce(t.vl_coparticipacao, 0) vl_coparticipacao,
			t.nr_seq_segurado,
			t.nr_seq_conta_copartic,
			t.nr_seq_conta,
			coalesce(t.nr_seq_plano, a.nr_seq_plano) nr_seq_plano,
			coalesce(t.ie_tipo_segurado, coalesce(a.ie_tipo_segurado,'B')) ie_tipo_segurado,
			a.ie_tipo_vinculo_operadora ie_tipo_vinculo_operadora,
			a.nr_seq_contrato nr_seq_contrato,
			a.nr_seq_pagador nr_seq_pagador,
			a.nr_seq_intercambio nr_seq_intercambio,
			b.ie_preco ie_preco,
			b.ie_regulamentacao ie_regulamentacao,
			b.ie_tipo_contratacao ie_tipo_contratacao,
			c.nr_seq_faixa_etaria nr_seq_faixa_etaria,
			a.ie_situacao_trabalhista ie_situacao_trabalhista,
			(	SELECT	x.nr_seq_congenere
				from	pls_segurado_repasse	x
				where	x.nr_seq_segurado	= t.nr_seq_segurado
				and	t.dt_contabilizacao between x.dt_repasse and coalesce(x.dt_fim_repasse, clock_timestamp())
			 LIMIT 1) nr_seq_congenere_repasse,
			case when (a.nr_seq_contrato IS NOT NULL AND a.nr_seq_contrato::text <> '')
					then CASE WHEN coalesce(e.cd_cgc_estipulante::text, '') = '' THEN  'PF'  ELSE 'PJ' END 
				else (case when (a.nr_seq_intercambio IS NOT NULL AND a.nr_seq_intercambio::text <> '')
						then CASE WHEN coalesce(f.cd_cgc::text, '') = '' THEN  'PF'  ELSE 'PJ' END 
					else null
				end)
			end ie_tipo_contrato,
			f.nr_seq_grupo_intercambio nr_seq_grupo_intercambio
		FROM (	select	sum(e.vl_coparticipacao) vl_coparticipacao,
				c.nr_seq_segurado,
				e.nr_sequencia nr_seq_conta_copartic,
				e.nr_seq_conta,
				c.nr_seq_plano,
				a.dt_contabilizacao,
				f.ie_tipo_segurado
			from	pls_lote_mensalidade		a,
				pls_mensalidade			b,
				pls_mensalidade_segurado	c,
				pls_mensalidade_seg_item	d,
				pls_conta_coparticipacao	e,
				pls_conta			f
			where	a.ie_status		= 2
			and	b.dt_referencia between dt_inicio_p and dt_fim_p
			and	coalesce(b.ie_cancelamento::text, '') = ''
			and	a.nr_sequencia		= b.nr_seq_lote
			and	f.nr_sequencia		= e.nr_seq_conta
			and	b.nr_sequencia		= c.nr_seq_mensalidade
			and	c.nr_sequencia		= d.nr_seq_mensalidade_seg
			and	c.nr_sequencia		= e.nr_seq_mensalidade_seg
			and	e.nr_seq_conta		= d.nr_seq_conta
			and	ie_data_coparticipacao_p = 'M' --Mensalidade

			group by
				c.nr_seq_segurado,
				e.nr_sequencia,
				e.nr_seq_conta,
				c.nr_seq_plano,
				a.dt_contabilizacao,
				f.ie_tipo_segurado
			
union all

			select	sum(a.vl_coparticipacao) vl_coparticipacao,
				b.nr_seq_segurado,
				a.nr_sequencia nr_seq_conta_copartic,
				b.nr_sequencia nr_seq_conta,
				b.nr_seq_plano,
				c.dt_mes_competencia dt_contabilizacao,
				b.ie_tipo_segurado
			from	pls_protocolo_conta		c,
				pls_conta			b,
				pls_conta_coparticipacao	a
			where	c.dt_mes_competencia between dt_inicio_p and dt_fim_p
			and	c.ie_tipo_protocolo	in ('C','F','I')
			and	c.nr_sequencia		= b.nr_seq_protocolo
			and	b.nr_sequencia		= a.nr_seq_conta
			and	ie_data_coparticipacao_p = 'P' --Protocolo de contas medicas

			group by
				b.nr_seq_segurado,
				a.nr_sequencia,
				b.nr_sequencia,
				b.nr_seq_plano,
				c.dt_mes_competencia,
				b.ie_tipo_segurado
			
union all

			select	sum(a.vl_coparticipacao) vl_coparticipacao,
				b.nr_seq_segurado,
				a.nr_sequencia nr_seq_conta_copartic,
				b.nr_sequencia nr_seq_conta,
				b.nr_seq_plano,
				c.dt_mes_competencia dt_contabilizacao,
				b.ie_tipo_segurado
			from	pls_protocolo_conta		c,
				pls_conta			b,
				pls_conta_coparticipacao	a
			where	c.dt_mes_competencia between dt_inicio_p and dt_fim_p
			and	c.ie_tipo_protocolo	= 'R'
			and	c.nr_sequencia		= b.nr_seq_protocolo
			and	b.nr_sequencia		= a.nr_seq_conta
			group by
				b.nr_seq_segurado,
				a.nr_sequencia,
				b.nr_sequencia,
				b.nr_seq_plano,
				c.dt_mes_competencia,
				b.ie_tipo_segurado) t, pls_plano b, pls_segurado a
LEFT OUTER JOIN pls_contrato e ON (a.nr_seq_contrato = e.nr_sequencia)
LEFT OUTER JOIN pls_intercambio f ON (a.nr_seq_intercambio = f.nr_sequencia)
LEFT OUTER JOIN pls_tabela_preco c ON (a.nr_seq_tabela = c.nr_sequencia)
WHERE a.nr_sequencia		= t.nr_seq_segurado and a.cd_estabelecimento 	= cd_estabelecimento_p and b.nr_sequencia		= t.nr_seq_plano;
		
		commit;
	exception
		when no_data_found then null;
	end;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_ar_gerar_resultado_pck.selecionar_coparticipacoes ( nr_seq_lote_p pls_ar_lote.nr_sequencia%type, dt_inicio_p pls_ar_lote.dt_inicio%type, dt_fim_p pls_ar_lote.dt_fim%type, ie_data_coparticipacao_p pls_parametros_ar.ie_data_coparticipacao%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) FROM PUBLIC;
