CREATE OR REPLACE PROCEDURE pls_ar_gerar_resultado_pck.validar_geracao_lote ( nr_seq_lote_p pls_ar_lote.nr_sequencia%type) AS $body$
DECLARE

nr_seq_lote_exec_w	pls_ar_lote.nr_sequencia%type;

BEGIN

-- Verifica se existe algum lote com dt inicio informada, mas sem dt fim, indicando um lote iniciado e nao concluido;

begin
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_lote_exec_w
	from	pls_ar_lote
	where	ie_situacao	= 'G';
exception
	when no_data_found then nr_seq_lote_exec_w := null;
end;

-- Se existir algum lote em execucao

if (coalesce(nr_seq_lote_exec_w, 0) <> 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(438833,'nr_seq_LOTE=' || to_char(nr_seq_lote_exec_w));
end if; -- fim achou lote em execucao
-- Verifica se existe algum lote que tenha data de inicio informado, mas nao tenha data de fim, indicando que existe um lote em geracao


END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
