		end if;
	elsif (ie_tipo_contratacao_p = 'CE') then
		if (current_setting('pls_atualizar_codificacao_pck.ie_colet_empres_w')::coalesce(varchar(2)::text, '') = '') then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(221912);
			/* Mensagem: Não foi encontrada uma regra de codificação do tipo "Tipo de contratação" para produtos coletivos empresariais! Favor verificar. */

		else
			return current_setting('pls_atualizar_codificacao_pck.ie_colet_empres_w')::varchar(2);
		end if;
	elsif (ie_tipo_contratacao_p = 'CA') then
		if (current_setting('pls_atualizar_codificacao_pck.ie_colet_adesao_w')::coalesce(varchar(2)::text, '') = '') then
