CREATE OR REPLACE FUNCTION pls_atualizar_codificacao_pck.get_tipo_pessoa_contrato (ie_tipo_pessoa_p text) RETURNS varchar AS $body$
BEGIN
	if (ie_tipo_pessoa_p = 'PF') then
		if (current_setting('pls_atualizar_codificacao_pck.ie_contrato_pf_w')::coalesce(varchar(2)::text, '') = '') then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(221917);
			/* Mensagem: Não foi encontrada uma regra de codificação do tipo "Tipo de contrato (Pessoa física ou Jurídica)" para pessoa física! Favor verificar. */

		else
			return current_setting('pls_atualizar_codificacao_pck.ie_contrato_pf_w')::varchar(2);
		end if;
	elsif (ie_tipo_pessoa_p = 'PJ') then
		if (current_setting('pls_atualizar_codificacao_pck.ie_contrato_pj_w')::coalesce(varchar(2)::text, '') = '') then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(221918);
			/* Mensagem: Não foi encontrada uma regra de codificação do tipo "Tipo de contrato (Pessoa física ou Jurídica)" para pessoa jurídica! Favor verificar. */

		else
			return current_setting('pls_atualizar_codificacao_pck.ie_contrato_pj_w')::varchar(2);
		end if;
	else
		return 'TP';
	end if;
	end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
