SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_atualizar_mes_reaj_pck.alterar_mes_reaj_grupo_contr ( nr_seq_grupo_contrato_p pls_grupo_contrato.nr_sequencia%type, nr_mes_reaj_grupo_cont_p pls_grupo_contrato.nr_mes_reajuste%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


ds_mensagem_w			pls_grupo_contrato_hist.ds_historico%type;
nr_mes_reaj_grupo_cont_w	pls_grupo_contrato.nr_mes_reajuste%type;

C01 CURSOR(	nr_seq_grupo_contrato_pc	pls_grupo_contrato.nr_sequencia%type) FOR
	SELECT	a.nr_seq_contrato,
		a.nr_seq_intercambio
	from	pls_contrato_grupo a
	where	a.ie_reajuste_grupo = 'S'
	and	a.nr_seq_grupo = nr_seq_grupo_contrato_pc;

BEGIN
if (coalesce(nr_mes_reaj_grupo_cont_p, 0) > 0) then
	nr_mes_reaj_grupo_cont_w	:= nr_mes_reaj_grupo_cont_p;
else
	nr_mes_reaj_grupo_cont_w	:= null;
end if;

update	pls_grupo_contrato
set	nr_mes_reajuste	= nr_mes_reaj_grupo_cont_w,
	dt_reajuste	= CASE WHEN nr_mes_reaj_grupo_cont_w = NULL THEN null  ELSE add_months(trunc(clock_timestamp(),'year'),nr_mes_reaj_grupo_cont_w-1) END ,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_grupo_contrato_p;

for r_c01_w in c01(nr_seq_grupo_contrato_p) loop
	begin
	if (r_c01_w.nr_seq_contrato IS NOT NULL AND r_c01_w.nr_seq_contrato::text <> '') then
		CALL pls_atualizar_mes_reaj_pck.aprovar_contrato(r_c01_w.nr_seq_contrato, nm_usuario_p, cd_estabelecimento_p);
	elsif (r_c01_w.nr_seq_intercambio IS NOT NULL AND r_c01_w.nr_seq_intercambio::text <> '') then
		CALL pls_atualizar_mes_reaj_pck.aprovar_contrato_intercambio(r_c01_w.nr_seq_intercambio, nm_usuario_p, cd_estabelecimento_p);
	end if;
	end;
end loop; --C01
if (nr_mes_reaj_grupo_cont_w IS NOT NULL AND nr_mes_reaj_grupo_cont_w::text <> '') then
	ds_mensagem_w	:= 'Alterado o mês de reajuste para o mês: ' || nr_mes_reaj_grupo_cont_w;
else
	ds_mensagem_w	:= 'Foi retirado o mês de reajuste do grupo de contrato.';
end if;

CALL pls_atualizar_mes_reaj_pck.inserir_historico_grupo(nr_seq_grupo_contrato_p, ds_mensagem_w, 'Alteração do mês de reajuste',
			'S', cd_estabelecimento_p, nm_usuario_p);


END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
