CREATE OR REPLACE PROCEDURE pls_atualizar_mes_reaj_pck.aprovar_contrato_intercambio ( nr_seq_intercambio_p pls_intercambio.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


dt_reajuste_w			pls_intercambio.dt_inclusao%type;
nr_seq_grupo_w			pls_grupo_contrato.nr_sequencia%type;
nr_mes_reajuste_grupo_w		bigint;
nr_seq_grupo_intercambio_w	pls_intercambio.nr_seq_grupo_intercambio%type;
dt_reajuste_grupo_int_w		pls_regra_grupo_inter.dt_reajuste%type;


BEGIN
select	dt_inclusao,
	nr_seq_grupo_intercambio
into STRICT	dt_reajuste_w,
	nr_seq_grupo_intercambio_w
from	pls_intercambio
where	nr_sequencia = nr_seq_intercambio_p;

if (nr_seq_grupo_intercambio_w IS NOT NULL AND nr_seq_grupo_intercambio_w::text <> '') then
	select	max(dt_reajuste)
	into STRICT	dt_reajuste_grupo_int_w
	from	pls_regra_grupo_inter
	where	nr_sequencia = nr_seq_grupo_intercambio_w;
end if;

if (coalesce(dt_reajuste_grupo_int_w::text, '') = '') then
	SELECT * FROM pls_atualizar_mes_reaj_pck.verificar_grupo_contrato(null, nr_seq_intercambio_p, nr_seq_grupo_w, nr_mes_reajuste_grupo_w) INTO STRICT nr_seq_grupo_w, nr_mes_reajuste_grupo_w;
end if;

CALL pls_atualizar_mes_reaj_pck.atualizar_mes_reaj_intercambio(	nr_seq_intercambio_p, coalesce(nr_mes_reajuste_grupo_w, (to_char(coalesce(dt_reajuste_grupo_int_w, dt_reajuste_w), 'mm'))::numeric ), nr_seq_grupo_w,
				'N', 'S', nm_usuario_p,
				cd_estabelecimento_p);

END;

$body$
