	current_setting('pls_atualizar_mes_reaj_pck.tb_nr_seq_intercambio_w')::pls_util_cta_pck.t_number_table(current_setting('pls_atualizar_mes_reaj_pck.nr_indice_att_mes_interc_w')::integer)	:= nr_seq_intercambio_p;
	current_setting('pls_atualizar_mes_reaj_pck.tb_nr_mes_reajuste_interc_w')::pls_util_cta_pck.t_number_table(current_setting('pls_atualizar_mes_reaj_pck.nr_indice_att_mes_interc_w')::integer)	:= nr_mes_reajuste_p;
	current_setting('pls_atualizar_mes_reaj_pck.tb_nr_seq_grupo_reaj_int_w')::pls_util_cta_pck.t_number_table(current_setting('pls_atualizar_mes_reaj_pck.nr_indice_att_mes_interc_w')::integer)	:= nr_seq_grupo_p;

	CALL pls_atualizar_mes_reaj_pck.vetor_atualizar_mes_interc(nm_usuario_p, ie_att_grupo_p, 'N');
