CREATE OR REPLACE PROCEDURE pls_atualizar_mes_reaj_pck.atualizar_mes_reaj_contrato ( nr_seq_contrato_p pls_contrato.nr_sequencia%type, nr_mes_reajuste_p pls_contrato.nr_mes_reajuste%type, nr_seq_grupo_p pls_grupo_contrato.nr_sequencia%type, ie_att_grupo_p text, ie_vetor_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN
if (ie_vetor_p = 'S') then
	current_setting('pls_atualizar_mes_reaj_pck.tb_nr_seq_contrato_w')::pls_util_cta_pck.t_number_table(current_setting('pls_atualizar_mes_reaj_pck.nr_indice_att_mes_contrato_w')::integer)		:= nr_seq_contrato_p;
	current_setting('pls_atualizar_mes_reaj_pck.tb_nr_mes_reajuste_contrato_w')::pls_util_cta_pck.t_number_table(current_setting('pls_atualizar_mes_reaj_pck.nr_indice_att_mes_contrato_w')::integer)	:= nr_mes_reajuste_p;
	current_setting('pls_atualizar_mes_reaj_pck.tb_nr_seq_grupo_contrato_w')::pls_util_cta_pck.t_number_table(current_setting('pls_atualizar_mes_reaj_pck.nr_indice_att_mes_contrato_w')::integer)	:= nr_seq_grupo_p;

	CALL pls_atualizar_mes_reaj_pck.vetor_att_mes_reaj_contrato(nm_usuario_p, ie_att_grupo_p, 'N');
elsif (ie_vetor_p = 'N') then
	update	pls_contrato
	set	nr_mes_reajuste 	= nr_mes_reajuste_p,
		nr_seq_grupo_reajuste 	= CASE WHEN ie_att_grupo_p='S' THEN  nr_seq_grupo_p  ELSE nr_seq_grupo_reajuste END ,
		nm_usuario		= nm_usuario_p
