CREATE OR REPLACE PROCEDURE pls_atualizar_mes_reaj_pck.atualizar_mes_reajuste_benef ( nr_seq_contrato_p pls_contrato.nr_sequencia%type, nr_seq_segurado_p pls_segurado.nr_sequencia%type, ie_reajuste_p pls_contrato.ie_reajuste%type, nr_mes_reaj_contrato_p pls_contrato.nr_mes_reajuste%type, ie_vetor_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_mes_reajuste_benef_w	pls_segurado.nr_mes_reajuste%type;

C01 CURSOR FOR
	SELECT	nr_sequencia nr_seq_segurado,
		coalesce(dt_reajuste, dt_contratacao) dt_reajuste_benef,
		coalesce(nr_mes_reajuste, 0) nr_mes_reajuste
	from	pls_segurado
	where	nr_seq_contrato = nr_seq_contrato_p
	and	coalesce(dt_cancelamento::text, '') = ''
	and	coalesce(nr_seq_segurado_p::text, '') = ''
	
union all

	SELECT	nr_sequencia nr_seq_segurado,
		coalesce(dt_reajuste, dt_contratacao) dt_reajuste_benef,
		coalesce(nr_mes_reajuste, 0) nr_mes_reajuste
	from	pls_segurado
	where	nr_sequencia = nr_seq_segurado_p
	and	coalesce(dt_cancelamento::text, '') = '';

BEGIN

for r_c01_w in c01 loop
	begin
	if (ie_reajuste_p = 'C') then
		nr_mes_reajuste_benef_w	:= nr_mes_reaj_contrato_p;
	elsif (ie_reajuste_p = 'A') then
		nr_mes_reajuste_benef_w := (to_char(r_c01_w.dt_reajuste_benef, 'mm'))::numeric;
	end if;

	if (r_c01_w.nr_mes_reajuste <> nr_mes_reajuste_benef_w) then
		CALL pls_atualizar_mes_reaj_pck.att_mes_reajuste_benef(r_c01_w.nr_seq_segurado, nr_mes_reajuste_benef_w, ie_vetor_p, nm_usuario_p);
		CALL pls_atualizar_mes_reaj_pck.atualizar_mes_reajuste_sca(r_c01_w.nr_seq_segurado, null, nr_mes_reajuste_benef_w, ie_vetor_p, nm_usuario_p);
	end if;
	end;
end loop; --c01
END;

$body$
