SET client_encoding TO 'UTF8';


-- Carregar variaveis necessarias para PCK



CREATE OR REPLACE PROCEDURE pls_cargas_pck.carregar_variaveis ( nr_seq_cg_lote_p pls_cg_lote.nr_sequencia%type, nr_seq_regra_p pls_cg_regra.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

				
ie_prefixo_view_w	varchar(255) := '_v';


BEGIN
-- Pegar dados do lote e regra

if (nr_seq_cg_lote_p IS NOT NULL AND nr_seq_cg_lote_p::text <> '') then
	select	max(r.ie_tipo_carga),
		max(r.nr_sequencia),
		max(l.nr_sequencia),
		nm_usuario_p,
		max(l.cd_estabelecimento)
	into STRICT	ie_tipo_carga_w,
		nr_seq_regra_w,
		nr_seq_cg_lote_w,
		nm_usuario_w,
		cd_estabelecimento_w
	from	pls_cg_lote	l,
		pls_cg_regra	r
	where	l.nr_seq_regra	= r.nr_sequencia
	and	l.nr_sequencia	= nr_seq_cg_lote_p;

-- Pegar dados da regra

elsif (nr_seq_regra_p IS NOT NULL AND nr_seq_regra_p::text <> '') then
	select	max(r.ie_tipo_carga),
		max(r.nr_sequencia),
		null,
		nm_usuario_p,
		max(r.cd_estabelecimento)
	into STRICT	ie_tipo_carga_w,
		nr_seq_regra_w,
		nr_seq_cg_lote_w,
		nm_usuario_w,
		cd_estabelecimento_w
	from	pls_cg_regra	r
	where	r.nr_sequencia	= nr_seq_regra_p;
	
	ie_prefixo_view_w := '_val_v';
end if;

-- Gerar o nome da VIEW

begin
select	upper(nm_tabela || ie_prefixo_view_w)
into STRICT	nm_view_carga_w
from	pls_cg_tab_tasy
where	ie_tipo_carga	= ie_tipo_carga_w
and	coalesce(nr_seq_tab_sup::text, '') = '';
exception
when others then
	nm_view_carga_w := upper('pls_cg_nova_'||ie_tipo_carga_w||ie_prefixo_view_w);
end;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
