SET client_encoding TO 'UTF8';


-- Efetuar a carga dos dados



CREATE OR REPLACE PROCEDURE pls_cargas_pck.importar_dados () AS $body$
DECLARE


ds_insert_w			text;
ds_select_w			text;

C01 CURSOR( ie_tipo_carga_pc		pls_cg_regra.ie_tipo_carga%type ) FOR
	SELECT	upper(nm_tabela) nm_tabela,
		to_char(nr_sequencia) ie_tipo_registro
	from	pls_cg_tab_tasy
	where	ie_tipo_carga = ie_tipo_carga_pc
	order by ie_tipo_registro;

BEGIN

for r_C01_w in C01( ie_tipo_carga_w ) loop
	-- Obter dados para importacao

	ds_insert_w := pls_cargas_pck.obter_estrutura_acao(r_C01_w.nm_tabela ,'I');
	ds_select_w := pls_cargas_pck.obter_estrutura_acao(r_C01_w.nm_tabela ,'V') || ' from '||nm_view_carga_w||' a where a.ie_tipo_registro = '|| chr(39) ||r_C01_w.ie_tipo_registro|| chr(39);
	
	-- Inserir os dados de carga

	EXECUTE to_char(ds_insert_w || ds_select_w);
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
