CREATE OR REPLACE FUNCTION pls_cartao_integracao_pck.buscar_url (cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

        -- Buscar url por estabelecimento

        c01 CURSOR FOR
            SELECT ds_servico_url from pls_cartao_parametro where cd_estabelecimento = cd_estabelecimento_p;

        -- Buscar  url generica para todos os estabelecimentos

        c02 CURSOR FOR
            SELECT ds_servico_url from pls_cartao_parametro where coalesce(cd_estabelecimento::text, '') = '';

        achou_w          boolean;
        ds_servico_url_w pls_cartao_parametro.ds_servico_url%type;

BEGIN
        open c01;
        fetch c01
            into ds_servico_url_w;
        achou_w := c01%found;
        close c01;
        if achou_w then
            return ds_servico_url_w;
        end if;
        open c02;
        fetch c02
            into ds_servico_url_w;
        close c02;
        return ds_servico_url_w;
    end;

