CREATE OR REPLACE PROCEDURE pls_chamado_pck.assumir_etapa ( nr_seq_chamado_etapa_p pls_chamado_etapa.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

nr_seq_chamado_w	pls_chamado.nr_sequencia%type;
nr_seq_tipo_etapa_w	pls_tipo_chamado_etapa.nr_sequencia%type;

BEGIN
select	nr_seq_chamado,
	nr_seq_tipo_etapa
into STRICT	nr_seq_chamado_w,
	nr_seq_tipo_etapa_w
from	pls_chamado_etapa
where	nr_sequencia	= nr_seq_chamado_etapa_p;

update	pls_chamado_etapa
set	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario_responsavel = nm_usuario_p
where	nr_sequencia	= nr_seq_chamado_etapa_p;

update	pls_chamado
set	ie_status	= 2,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_chamado_w;

CALL pls_chamado_pck.atualizar_estagio_chamado(nr_seq_chamado_w,	nr_seq_tipo_etapa_w);

commit;
END;

$body$
