SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_chamado_pck.gerar_proxima_etapa ( nr_seq_chamado_p pls_chamado.nr_sequencia%type, nr_seq_tipo_chamado_p pls_tipo_chamado.nr_sequencia%type, nr_seq_chamado_etapa_p pls_chamado_etapa.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) AS $body$
DECLARE


nr_seq_tipo_chamado_w		pls_tipo_chamado.nr_sequencia%type;
dt_prazo_w			pls_chamado_etapa.dt_prazo%type;
nr_seq_tipo_chamado_etapa_w	pls_tipo_chamado_etapa.nr_sequencia%type;
nr_seq_proxima_etapa_w		pls_tipo_chamado_etapa.nr_sequencia%type;
cd_perfil_w			pls_tipo_chamado_etapa.cd_perfil%type;
nr_ordem_w			pls_tipo_chamado_etapa.nr_ordem%type := 0;
nr_seq_chamado_w		pls_chamado.nr_sequencia%type;


BEGIN

if (nr_seq_chamado_etapa_p IS NOT NULL AND nr_seq_chamado_etapa_p::text <> '') then
	select	a.nr_seq_chamado,
		a.nr_seq_proxima_etapa,
		b.nr_seq_tipo_chamado
	into STRICT	nr_seq_chamado_w,
		nr_seq_tipo_chamado_etapa_w,
		nr_seq_tipo_chamado_w
	from	pls_chamado_etapa a,
		pls_tipo_chamado_etapa b
	where	b.nr_sequencia	= a.nr_seq_tipo_etapa
	and	a.nr_sequencia	= nr_seq_chamado_etapa_p;
	
	CALL pls_chamado_pck.atualizar_estagio_chamado(nr_seq_chamado_w,	nr_seq_tipo_chamado_etapa_w);
elsif (nr_seq_chamado_p IS NOT NULL AND nr_seq_chamado_p::text <> '') then
	nr_seq_chamado_w	:= nr_seq_chamado_p;
	nr_seq_tipo_chamado_w	:= nr_seq_tipo_chamado_p;
	
	select	nr_sequencia
	into STRICT	nr_seq_tipo_chamado_etapa_w
	from	pls_tipo_chamado_etapa
	where	nr_seq_tipo_chamado	= nr_seq_tipo_chamado_w
	and	nr_ordem = (	SELECT	min(nr_ordem)
				from	pls_tipo_chamado_etapa
				where	nr_seq_tipo_chamado	= nr_seq_tipo_chamado_w);
end if;

select	cd_perfil,
	CASE WHEN ie_tipo_prazo='H' THEN  clock_timestamp()+(qt_prazo/24)  ELSE fim_dia(clock_timestamp()+qt_prazo) END ,
	nr_ordem
into STRICT	cd_perfil_w,
	dt_prazo_w,
	nr_ordem_w
from	pls_tipo_chamado_etapa
where	nr_sequencia = nr_seq_tipo_chamado_etapa_w;

select	max(nr_sequencia)
into STRICT	nr_seq_proxima_etapa_w
from	pls_tipo_chamado_etapa
where	nr_seq_tipo_chamado	= nr_seq_tipo_chamado_w
and	nr_ordem = (	SELECT	min(nr_ordem)
			from	pls_tipo_chamado_etapa
			where	nr_seq_tipo_chamado	= nr_seq_tipo_chamado_w
			and	nr_ordem > nr_ordem_w);

insert	into	pls_chamado_etapa(	nr_sequencia,
		nr_seq_chamado,
		nr_seq_tipo_etapa,
		dt_etapa,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_prazo,
		nr_seq_proxima_etapa,
		cd_perfil)
	values (nextval('pls_chamado_etapa_seq'),
		nr_seq_chamado_w,
		nr_seq_tipo_chamado_etapa_w,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		dt_prazo_w,
		nr_seq_proxima_etapa_w,
		cd_perfil_w);

if (ie_commit_p = 'S') then
	commit;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_chamado_pck.gerar_proxima_etapa ( nr_seq_chamado_p pls_chamado.nr_sequencia%type, nr_seq_tipo_chamado_p pls_tipo_chamado.nr_sequencia%type, nr_seq_chamado_etapa_p pls_chamado_etapa.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) FROM PUBLIC;
